import { useEffect, useState } from "react";

export default function MyCart(props) {
    const items = props.purchaseItem;
    const [total, setTotal] = useState(0);

    useEffect(() => {
        const calculatedTotal = items.reduce((sum, item) => sum + item.price, 0);
        setTotal(calculatedTotal);
    }, [items]);

    const handleRemoveFromCart = (index) => {
        // Handle removing the item from the cart here
        const updatedItems = [...items];
        updatedItems.splice(index, 1);
        props.updateCart(updatedItems);
    }

    const handleIncreaseQuantity = (index) => {
        // Handle increasing the quantity of the item here
        const updatedItems = [...items];
        updatedItems[index].quantity += 1;
        props.updateCart(updatedItems);
    }

    const handleDecreaseQuantity = (index) => {
        // Handle decreasing the quantity of the item here
        const updatedItems = [...items];
        if (updatedItems[index].quantity > 1) {
            updatedItems[index].quantity -= 1;
            props.updateCart(updatedItems);
        }
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-6">
                    <h2 className="text-primary">Shopping Cart</h2>
                    <div className="card">
                        {items.map((item, index) => (
                            <div className="card-body" key={index}>
                                <div className="text-center">
                                    <img src={item.img} style={{ width: "150px" }} alt={item.name} />
                                </div>
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">Price: {item.price}</p>
                                <p className="card-text">Quantity: {item.quantity}</p>
                                <button className="btn btn-danger" onClick={() => handleRemoveFromCart(index)}>
                                    Remove from Cart
                                </button>
                                <button className="btn btn-success" onClick={() => handleIncreaseQuantity(index)}>
                                    Increase Quantity
                                </button>
                                <button className="btn btn-warning" onClick={() => handleDecreaseQuantity(index)}>
                                    Decrease Quantity
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="col-sm-6">
                    <h2 className="text-primary">Your Order</h2>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Subtotal</th>
                            </tr>
                        </thead>
                        <tbody>
                            {items.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.name}</td>
                                    <td>{item.price * item.quantity}</td>
                                </tr>
                            ))}
                            <tr>
                                <td colSpan={2}>
                                    <strong>Total</strong>
                                </td>
                            </tr>
                            <tr>
                                <td colSpan={2}>
                                    {total}
                                </td>
                            </tr>
                        </tbody>
                    </table>
                    <button className="btn btn-outline-dark" onClick={props.placeOrder}>
                        Place the Order
                    </button>
                </div>
            </div>
        </div>
    );
}
