import React from 'react';
import {BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './component/Header';
import Home from './component/Home';
import './App.css';


function App() {

  state = {
    selectedOptions: [],
  };

  handleCheckboxChange = (event) => {
    const { value, checked } = event.target;
    this.setState((prevState) => {
      if (checked) {
        return { selectedOptions: [...prevState.selectedOptions, value] };
      } else {
        return {
          selectedOptions: prevState.selectedOptions.filter(
            (option) => option !== value
          ),
        };
      }
    });
  };

  render() 
    const itemList = [
      { id: 1, options: ['option1'], text: 'Item 1' },
      { id: 2, options: ['option2'], text: 'Item 2' },
      { id: 3, options: ['option1', 'option3'], text: 'Item 3' },
      // ...more items...
    ];

    const { selectedOptions } = this.state;
  return (
    <>
    <Router>
      <Header />
      <Routes>
          <Route path='/' element={<Home />} />
      </Routes>
    </Router>

    <div>
        <div id="filter-options">
          <label>
            <input
              type="checkbox"
              value="option1"
              onChange={this.handleCheckboxChange}
            />{' '}
            Option 1
          </label>
          <label>
            <input
              type="checkbox"
              value="option2"
              onChange={this.handleCheckboxChange}
            />{' '}
            Option 2
          </label>
          <label>
            <input
              type="checkbox"
              value="option3"
              onChange={this.handleCheckboxChange}
            />{' '}
            Option 3
          </label>
        </div>
        <ul id="item-list">
          {itemList.map((item) => (
            <li
              key={item.id}
              style={{
                display:
                  item.options.every((option) =>
                    selectedOptions.includes(option)
                  ) || selectedOptions.length === 0
                    ? 'block'
                    : 'none',
              }}
            >
              {item.text}
            </li>
          ))}
        </ul>
      </div>

    </>
  );
}

export default App;
